        # FEC Global Info
        p1 = re.compile(r'^(?P<key>[A-Za-z\s]+)$')

        # "ses_chunk_head": int
        # "pak_chunk_head": int
        p2 = re.compile(r'^(?P<key>[a-z\_]+)\s+:\s+(?P<value>[\d\D]+)')

