        # MLDSN Enabled : On
        # PIMSN Enabled : Off

        # Vlan 20
        p0 = re.compile(r'^Vlan\s+(?P<vlan>\d+)$')
        # MLDSN Enabled : On
        p1 = re.compile('MLDSN+  +Enabled  +: +(?P<mldsn_en>\w+)')

