        p4= re.compile('Oper+ +State+    +:+ +(?P<op_state>\w+)')

        # STP TCN Flood : Off
        p5= re.compile('STP+ +TCN+ +Flood+   +:+ +(?P<stp_tcn_flood>\w+)')

