        p4 = re.compile(r"^L2\s+validation\s+error:\s+(?P<L2_validation>\d+)$")

        # L2 matm add error: 0
        p5 = re.compile(r"^L2\s+matm\s+add\s+error:\s+(?P<L2_matm>\d+)$")

